import pages.system.LdapConfigPageimport spock.lang.Specificationimport spock.lang.Stepwise@Stepwiseclass LdapPageSpec extends AutoSignOnSignOffSpec { def setup () {        		drawerModule.goLdapConfig()    }    def cleanup() {        drawerModule.goHome()        at HomePage    }		def "can get to the ldap page"() {        given:        at LdapConfigPage        when:        drawerModule.goHome()        at HomePage        drawerModule.goLdapConfig()        then:        at LdapConfigPage		//isLdapAuthDisable()		//isSecureConncetionDisable()		//isAnonymous()    }		def "can configure ldap server with anonymous login and SSL  "() {        given:        at LdapConfigPage        when:				enableSSLConnection()		setLdapSettings(map)		saveLdapSettings()		        then:		at LdapConfigPage		getAlertMessageText() == "Ldap configuration successfully saved."		dismissAlert()				where: map << [[			'serverIpAddress':'Lexk',			'serverPort':'636',			'userSearchFilter':'OU=l',			'userSearchBase':'ou=y',			'anonymousLogin':'anonymous',						'searchBase':'ou=indiaLab'		],		[		'serverIpAddress':'indiadev',			'serverPort':'636',			'userSearchFilter':'ou=Indiadev',			'userSearchBase':'ou=indiaLab,test=Indialab',			'groupSearchFilter':'\\123',			'groupSearchBase':'lmc',			'groupIdentifier':'lmcdevUsers',			'memberOfGroups':'GroupAdmin',			'anonymousLogin':'anonymous',						'searchBase':'ou=lexmark,ou=lexteam,users=foo'		],		['serverIpAddress':'draco.dhcp.com',			'serverPort':'636',			'groupIdentifier':'lmcdevUsers',			'memberOfGroups':'GroupAdmin',			'anonymousLogin':'anonymous',						'searchBase':'ou=LEXMARK'		]]    }		def "can configure ldap server with anonymous login without SSL "() {        given:        at LdapConfigPage        when:				setLdapSettings(map)		saveLdapSettings()		        then:		at LdapConfigPage		getAlertMessageText() == "Ldap configuration successfully saved."		dismissAlert()				where: map << [[			'serverIpAddress':'Lexk',			'serverPort':'389',			'userSearchFilter':'OU=l',			'userSearchBase':'ou=y',			'anonymousLogin':'anonymous',			'searchBase':'ou=indiaLab'		],		[		'serverIpAddress':'indiadev',			'serverPort':'389',			'userSearchFilter':'ou=Indiadev',			'userSearchBase':'ou=indiaLab,test=Indialab',			'groupSearchFilter':'\\123',			'groupSearchBase':'lmc',			'groupIdentifier':'lmcdevUsers',			'anonymousLogin':'anonymous',			'memberOfGroups':'GroupAdmin',			'searchBase':'ou=lexmark,ou=lexteam,users=foo'		],		['serverIpAddress':'draco.dhcp.com',			'serverPort':'389',			'groupIdentifier':'lmcdevUsers',			'memberOfGroups':'GroupAdmin',			'anonymousLogin':'anonymous',			'searchBase':'ou=LEXMARK'		]]    }			def "can configure ldap server with user login without SSL "() {        given:        at LdapConfigPage        when:				setLdapSettings(map)		saveLdapSettings()		        then:		at LdapConfigPage		getAlertMessageText() == "Ldap configuration successfully saved."		dismissAlert()				where: map << [[			'serverIpAddress':'Lexk',			'serverPort':'389',			'userSearchFilter':'OU=l',			'userSearchBase':'ou=y',			'userLogin':'user',			'username':'0O7Admin',			'password':'secrectAgent',			'searchBase':'ou=indiaLab'		],		[		'serverIpAddress':'indiadev',			'serverPort':'389',			'userSearchFilter':'ou=Indiadev',			'userSearchBase':'ou=indiaLab,test=Indialab',			'groupSearchFilter':'\\123',			'groupSearchBase':'lmc',			'groupIdentifier':'lmcdevUsers',			'memberOfGroups':'GroupAdmin',			'userLogin':'user',			'username':'LdApAdmin',			'password':'perc007',			'searchBase':'ou=lexmark,ou=lexteam,users=foo'		],		['serverIpAddress':'draco.dhcp.com',			'serverPort':'389',			'groupIdentifier':'lmcdevUsers',			'memberOfGroups':'GroupAdmin',			'userLogin':'user',			'username':'ADS-Admin',			'password':'noGuess',			'searchBase':'ou=LEXMARK'		]]    }}